import React, { useState } from "react";
import "./app.scss";
import {
  Form,
  FormGroup,
  Select,
  SelectItem,
  Button,
  Loading,
} from "carbon-components-react";
import { years, months, costCentres, accounts } from "./utils";

function App() {
  const [year, setYear] = useState(2020);
  const [month, setMonth] = useState("Jan");
  const [costCentre, setCostCentre] = useState("CC101");
  const [account, setAccount] = useState(1000000);

  return (
    <div className="App">
      <div className="mainContainer">
        <Form>
          <FormGroup>
            <Select
              id="select-0"
              labelText="Select Year"
              onChange={(e) => setYear(e.target.value)}
            >
              {years.map((yearVal) => (
                <SelectItem text={yearVal} value={yearVal} />
              ))}
            </Select>
          </FormGroup>
          <FormGroup>
            <Select
              id="select-1"
              labelText="Select Month"
              onChange={(e) => setMonth(e.target.value)}
            >
              {months.map((monthVal) => (
                <SelectItem text={monthVal} value={monthVal} />
              ))}
            </Select>
          </FormGroup>
          <FormGroup>
            <Select
              id="select-2"
              labelText="Select Cost Centre"
              onChange={(e) => setCostCentre(e.target.value)}
            >
              {costCentres.map((ccVal) => (
                <SelectItem text={ccVal} value={ccVal} />
              ))}
            </Select>
          </FormGroup>
          <FormGroup>
            <Select
              id="select-3"
              labelText="Select Account"
              onChange={(e) => setAccount(e.target.value)}
            >
              {accounts.map((accVal) => (
                <SelectItem text={accVal} value={accVal} />
              ))}
            </Select>
          </FormGroup>
          <Button>Predict</Button>
        </Form>
      </div>
    </div>
  );
}

export default App;
